using System.Diagnostics;
using Microsoft.AspNetCore.Mvc;
using Test1.Models;

namespace Test1.Controllers;

public class MayusController : Controller
{
    public string mayuscula(string texto )
    {
        string resultado = texto.ToUpper();

        char[] copia = resultado.ToCharArray();
        Array.Reverse(copia);
        string alreves = new string(copia);
        
        return alreves;
        
    }

    public string comparar(string cadena1, string cadena2)
    {
        string palabra1 = cadena1.ToUpper();
        string palabra2 = cadena2.ToUpper();

        int result = palabra1.CompareTo(palabra2);

        if (result == 0)
            return "Las palabras son iguales";
        else
            return "Las palabras son distintas";
    }
    public IActionResult Perfil()
    {
        var perfil = new Perfil(){
            nombre = "Cristopher Adonis Roa Gomez",
            edad = 21,
            carrera = "Ingenieria en sistemas", 
            foto = "data:image/jpeg;base64,"
        }; 
        return View(perfil);
    }
}
    